mapper.select:

select count(*) as acom_no_cnt

from tbl_acomodation

where acom_no = #{acom_no}

select acom_no, fk_district_no , fk_host_no , acom_name , acom_image , address , extra_address , sur_info , nvl(parking_info,'없음') as parking_info , acom_latitude , acom_longitude , fk_spec_no
from tbl_acomodation
where acom_no = #{value}
